import React from "react";
import { register } from "../API";

const Register = () => {
   function AuthForm({
     token,
     setToken,
     setUser,
     setIsLoggedIn,
   }) {
     const [username, setUserName] =useState("");
     const [password, setPassword] =useState("");

     async function handleSubmit(
       event
     ) {
       event.preventDefault();
       const user =
         {
           username,
           password,
         };
       const {
         data,
       } =
         await login(
           user
         );
       localStorage.token =
         data.token;
       setIsLoggedIn(
         true
       );
       setToken(
         localStorage.token
       );
       setUserName(
         ""
       );
       setPassword(
         ""
       );
     }

     return (
       <>
         <form onSubmit={handleSubmit}>
           <label className="flex">
            Username:
           <input type="text" value={username}
               onChange={(
                 event
               ) =>
                 setUserName(
                   event
                     .target
                     .value
                 )
               }
             ></input>
           </label>
           <label>
             Password:
             <input
               type="text"
               value={
                 password
               }
               onChange={(
                 event
               ) =>
                 setPassword(
                   event
                     .target
                     .value
                 )
               }
             ></input>
           </label>
           <button type="submit">
             Login
           </button>
         </form>
       </>
     );
   }
}

export default Register;
