import React, {useState}from "react";
import { register } from "../API";

const Register = () => {
  const [username, setUserName] = useState('');
  const [password, setPassword] = useState('');

  async function handleSubmit(event) {
    event.preventDefault();
    const user = {
      username,
      password,
    }
    const {data} = await register(user);

    return "Thanks for signing up! Feel free to login." + data.message
  }
  return (
    <>
      <h3>
        Please enter a username and password
      </h3>
      <form onSubmit={handleSubmit}>
        <label>Username:<input type='text' value={username} onChange={(event) => setUserName(event.target.value)}></input></label>
        <label>Password:<input type='text' value={password} onChange={(event) => setPassword(event.target.value)}></input></label>
        <button type='submit'>Register</button>
      </form>
    </>
  );
}

export default Register;
